### `internal/manifest/generator.go`
```go
package manifest

import (
    "context"
    "os"
    "path/filepath"
    "time"

    "github.com/g5becks/dox/internal/config"
    "github.com/g5becks/dox/internal/parser"
)

const (
    maxParseSize = 50 * 1024 * 1024 // 50MB
)

// Generate creates a manifest by walking the output directory and parsing files.
// It is non-fatal - errors on individual files are logged as warnings.
func Generate(ctx context.Context, cfg *config.Config) error {
    outputDir := cfg.Output
    m := New()

    // Build parser registry
    parsers := []parser.Parser{
        parser.NewMarkdownParser(),
        parser.NewMDXParser(),
        parser.NewTextParser(),
        parser.NewTypeScriptParser(),
    }

    // Walk each source's output directory
    for sourceName, sourceCfg := range cfg.Sources {
        sourceDir := resolveSourceDir(outputDir, sourceName, sourceCfg)

        if _, err := os.Stat(sourceDir); os.IsNotExist(err) {
            continue // Source not synced yet
        }

        collection := &Collection{
            Name:     sourceName,
            Type:     sourceCfg.Type,
            Source:   resolveSourceLocation(sourceCfg),
            Path:     sourceCfg.Path,
            Ref:      sourceCfg.Ref,
            LastSync: time.Now(),
        }

        // Walk directory and parse files
        err := filepath.WalkDir(sourceDir, func(path string, d os.DirEntry, err error) error {
            if err != nil || d.IsDir() {
                return err
            }

            // Skip manifest and lock files
            if d.Name() == ManifestFile || d.Name() == ".dox.lock" {
                return nil
            }

            relPath, _ := filepath.Rel(sourceDir, path)
            fileInfo, parseErr := parseFile(path, relPath, parsers)
            if parseErr != nil {
                // Non-fatal: log warning, skip file
                return nil
            }

            collection.Files = append(collection.Files, *fileInfo)
            collection.TotalSize += fileInfo.Size
            return nil
        })

        if err != nil {
            return err
        }

        collection.FileCount = len(collection.Files)
        m.Collections[sourceName] = collection
    }

    return m.Save(outputDir)
}

// parseFile reads a file and runs the appropriate parser.
func parseFile(absPath string, relPath string, parsers []parser.Parser) (*FileInfo, error) {
    // 1. Stat file for size and mod time
    // 2. If size > maxParseSize: return FileInfo with warning "file_too_large"
    // 3. Read file content
    // 4. If parser.IsBinary(content): skip (return error)
    // 5. Find matching parser via CanParse()
    // 6. If no parser matches: return basic FileInfo with type "unknown"
    // 7. Run parser.Parse()
    // 8. Build and return FileInfo with all metadata
}

// resolveSourceDir determines the output directory for a source.
func resolveSourceDir(outputDir string, name string, src config.Source) string {
    if src.Out != "" {
        return filepath.Join(outputDir, src.Out)
    }
    return filepath.Join(outputDir, name)
}

// resolveSourceLocation returns a human-readable source location.
func resolveSourceLocation(src config.Source) string {
    if src.Repo != "" {
        return src.Repo
    }
    return src.URL
}
